$(function(){$(".btn-thx").click(function(){var t=$(this).children("i:last-child"),n=$(this).parent().attr("data-id");t.text(Number(t.text())+1),$(this).unbind("click"),$.get("/article/add-thanks/"+n)}),$("#send").click(function(){var t=$("#content").val(),n=t.replace(/\ +/g,"");if(""==t||""==n)return $("#content").val("").attr("placeholder","老大，我很饿...."),!1;$("#message-form").submit()})});